import React,{ useContext, useState } from 'react';

import classes from './Cart.module.css';
import CartItem from './CartItem';
import Modal from '../UI/Modal';
import CartContext from '../store/cart-context'
import Checkout from './Checkout';

const Cart = (props) => {
  const [isCheckout, setIsCheckout] = useState(false);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [didSubmit,setDidSubmit]=useState(false);
  const cartCtx = useContext(CartContext);

  const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;

  const cartItemRemoveHandler = (id) => {
    cartCtx.removeItem(id);
  }

  const cartItemAddHandler = (item) => {
    cartCtx.addItem({ ...item, amount: 1 });
  }

  const orderHandler = () => {
    setIsCheckout(true);
  }

  const submitOrderHandler = async (userData) => {

    setIsSubmitting(true);

    await fetch('https://react-app-fd13e-default-rtdb.firebaseio.com/orders.json', {
      method: 'post',
      body: JSON.stringify({
        user: userData,
        orderedItems: cartCtx.items
      })
    })
    setIsSubmitting(false);
    setDidSubmit(true);
  };

  const cartItems = <ul className={classes['cart-items']}>{cartCtx.items.map((item) => <CartItem key={item.id} name={item.name} amount={item.amount} price={item.price} onRemove={cartItemRemoveHandler.bind(null, item.id)} onAdd={cartItemAddHandler.bind(null, item)} />)}</ul>;
  const modalActions = <div className={classes.actions}>
    <button className={classes['button--alt']} onClick={props.onHideCart}>Close</button>
    {hasItems && <button className={classes.button} onClick={orderHandler}>Order</button>}
  </div>

  const cartModalContent= <React.Fragment>
     {cartItems}
    <div className={classes.total}>
      <span>Total Amount</span>
      <span>{totalAmount}</span>
    </div>
    {isCheckout && < Checkout onConfirm={submitOrderHandler} onCancel={props.onHideCart} />}
    {!isCheckout && modalActions}
  </React.Fragment>

  const isSubmittingModalContent=<p>Sending order data...</p>

  const didSubmitModalContent=<p>Successfully sent the order</p>

  return <Modal onClose={props.onHideCart}>
     {!isSubmitting && !didSubmit && cartModalContent}
     {isSubmitting && isSubmittingModalContent}
     {didSubmit && !isSubmitting && didSubmitModalContent}
  </Modal>
}

export default Cart;
